class ToggleFollow{constructor(l){this.toggler=l,this.toggleFollow()}toggleFollow(){$(this.toggler).click((function(l){l.preventDefault();let t=this;$.ajax({type:"POST",url:$(t).attr("href")}).done((function(l){let o=parseInt($(t).attr("data-follows")),a=$(t).attr("href");console.log(o),1==l.data.deleted?(o-=1,$(t).attr("data-follows",o),$(t).html(`<a href="${a}" class="toggle-follow-button" data-follows="${o}"><button class="c_grey c_blue" id="follow_btn"><i class="fas fa-rss"></i> Follow</button></a>`)):(o+=1,$(t).attr("data-follows",o),$(t).html(`<a href="${a}" class="toggle-follow-button" data-follows=""><button class="c_grey" id="follow_btn"><i class="fas fa-check-square"></i> Following</button></a>`))})).fail((function(l){console.log("error in completing the request",l)}))}))}}